import React, { useState, useEffect } from "react";
import axios from "axios";
import {Routes, Route, useNavigate} from 'react-router-dom';
import {url} from '../App';

function AddClub(props){
  const [aCode, setACode] = useState("");
  const [clubName, setClubName] = useState("");
  let navigate = useNavigate();

  const handleSubmit = async e =>{
    e.preventDefault();
    const response = await axios.post(url+"/clubmembers", {
      params:{
        user_id: userID,
        club_id: clubID
      }
    }).then(response =>{
      if (response.joined_club == true) {
        alert("Club was successfully joined!");
        navigate("/ac")
      }
      else alert("Error: " + response.error);
    });

  };

  return (
    <div className='modal'>
      <div>
        <p>Add Club</p>
      </div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="addCode">Enter the add-code: </label>
        <input
          type="text"
          value={aCode}
          placeholder="enter your add-code"
          onChange={({ target }) => setACode(target.value)}
        />
        <button type ="submit">Enter</button>
      </form>
      <button className='btn btn--alt' onClick={props.onCancel}>Go Back</button>
    </div>
  );
}

export default AddClub;
