import React, {useState, useEffect} from "react";
import axios from "axios";
import {useNavigate} from 'react-router-dom';
import {getUser, resetUserSession} from "./Service/AuthService";

const deleterURL = "https://xhetcinp85.execute-api.us-west-1.amazonaws.com/prod/delete"

export const Delete = () => {
  const [password, setPassword] = useState("");
  const username = getUser().username;
  const navigate = useNavigate();

  // submit correct information to delete current account
  const handleSubmit = async e =>{
    e.preventDefault();
    if (password.trim() === ''){
      alert('All fields are required');
      return;
    }

    const requestConfig = {
      headers: {
        'x-api-key': 'n4mlBUgJbT3YSFHliX5tTeakXeB760x9GrdrVJIi'
      }
    }

    const requestBody = {
      username: username,
      password: password,
    }

    axios.delete(deleterURL, {
      data: requestBody,
      headers: requestConfig.headers
    })
      .then(response => {
      // go back to login upon account deletion
      alert("Account deletion successful!");
      resetUserSession();
      navigate("/");
    }).catch(error => {
      alert(error);
    });
  };

  return (
    <div className='modal'>
      <div>
        <p>Delete Account Verification</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="password">Password: </label>
          <input
            type="text"
            value={password}
            placeholder="enter your password"
            onChange={({ target }) => setPassword(target.value)}
            />
        </div>
        <button className='btn btn--alt' type ="submit">Enter</button>
      </form>
      <button className='btn btn--alt' onClick = {() => navigate(-1)}>Go Back</button>
    </div>
  );
};

export default Delete;
