import React, { useState, useEffect } from "react";
import axios from "axios";
import {useNavigate} from 'react-router-dom';
import {url} from '../../App';
import {useParams} from "react-router-dom";

function EventMaker(props){
  let {clubID} = useParams();
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [capacity, setCapacity] = useState("");
  const [plannedDate, setPlannedDate] = useState("");
  // used to help formate plannedDate
  const [dateDay, setDateDay] = useState("");
  const [dateTime, setdateTime] = useState("");

  const handleSubmit = async e =>{
    e.preventDefault();
    const response = await axios.post("http://localhost:3001/clubs", {
      user_id: JSON.parse(localStorage.getItem("user")),
      club_id: clubID,
      title: title,
      description: desc,
      event_capacity: capacity,
      planned_date: plannedDate,
    });

  };


  return (
    <div className='modal'>
      <div>
        <p>Create Event</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Event Name: </label>
          <input
            type="text"
            value={eventName}
            placeholder="enter the event name"
            onChange={({ target }) => setEventName(target.value)}
            />
        </div>
        <div>
        <label htmlFor="eventDate">Date: </label>
        <input
          type="text"
          value={eventName}
          placeholder="enter the date"
          onChange={({ target }) => setEventDate(target.value)}
          />
        </div>
        <div>
          <label htmlFor="loc">Event Location: </label>
          <input
            type="text"
            value={eventName}
            placeholder="enter the event location"
            onChange={({ target }) => setEventLoc(target.value)}
            />
        </div>
        <div>
        <label htmlFor="eventDesc">Event Description: </label>
        <input
          type="text"
          value={eventDesc}
          placeholder="enter your event description"
          onChange={({ target }) => setEventDesc(target.value)}
          />
        </div>
        <button type ="submit">Enter</button>
      </form>
    </div>
  );
}

export default EventMaker;
