import {useParams} from "react-router-dom";
import React, { useState, useEffect } from "react";
import axios from "axios";
import EventMaker from './EventMaker';
import {url} from '../App';


export const ViewClubSingle = () => {
  let {clubID} = useParams();
  //club's event parameters
  const [events, setEvents] = useState([]);
  const [filter, setFilter] = useState("");
  const [limit, setLimit] = useState("");


  useEffect(() => {
    async function getEvents(){
      const response = await axios.get(url+"/event", {
        params:{
          user_id: localStorage.getItem("user"),
          club_id: clubID,
          filter: filter,
          limit: limit
        }
      }).then(resposne => {
        if (response.error == "") setEvents(response.event_list);
        else alert("Error: " + response.error);
      }).catch (error => {
        alert(error);
        navigate(-1);
      });
    };
    getEvents();
  }, [])

  const handleJoinStatus = async e =>{
    e.preventDefault();
    const response = await axios.posrt(url+"/event", {
      params:{
        user_id: localStorage.getItem("user"),
        club_id: clubID,
        event_id: ad
      }
    })
  }

  return (
    <div>
        <div>
          <form>
            <label htmlFor="filter">Search by name: </label>
            <input
              type="text"
              value={filter}
              placeholder="search club by name"
              onChange={({ target }) => setFilter(target.value)}
              />
            <button type ="submit">Enter</button>
          </form>
        </div>
        <div>
          <form>
            <label htmlFor="limit">Change viewing limit: </label>
            <input
              type="text"
              value={limit}
              placeholder="change the viewing limit"
              onChange={({ target }) => setLimit(target.value)}
              />
            <button type ="submit">Enter</button>
          </form>
        </div>
        <div>
          <p>{clubID}'s Events:</p>
        </div>
        <div>
          {events.map((event) => (
          <div className = "card">
            <p>{event.title} (@{event.event_id})</p>
            <p>Organized By {event.club_id}</p>
            <p>Planned Date: {event.planned_date}</p>
            <p>Event Created: {event.date_generated}</p>
            <p>Capacity: {event.num_attending}/{event.event_capacity}</p>
            <div>
              <div>
                Description
              </div>
              <div>
                {event.description}
              </div>
            </div>
            <div>
              <div>
                People Involved
              </div>
              <div>
                {event.people_involved.map((person) => (
                  <p>(person)</p>
                ))}
              </div>
            </div>
            <button className='btn' onClick={handleJoinStatus}>Mark Event</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewClubSingle;
