import React, { useState, useEffect } from "react";
import axios from "axios";
import {Routes, Route, useNavigate} from 'react-router-dom';
import {getUser, getToken, setUserSession, resetUserSession} from './Service/AuthService';

const loginURL = "https://xhetcinp85.execute-api.us-west-1.amazonaws.com/prod/login"

export const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState();
  const navigate = useNavigate();

  // check if there is a user already logged in from  previous activity.

  // login the user after login-form completion
  const handleSubmit = async e => {
    e.preventDefault();
    if (username.trim() === '' || password.trim() === ''){
      alert('All fields are required');
      return;
    }
    const requestConfig = {
      headers: {
        'x-api-key': 'n4mlBUgJbT3YSFHliX5tTeakXeB760x9GrdrVJIi'
      }
    }
    const requestBody = {
      username: username,
      password: password
    }
    axios.post(loginURL, requestBody, requestConfig).then(response => {
      setUserSession(response.data.user, response.data.token);
        // go to homepage
        navigate("/home/ac");
    }).catch(error => {
      alert(error);
    });
  };

  const goRegister = () => {
    navigate("/register");
  };

  // if there's no user, show the login form
  return (
    <div className='card'>
      <div>
        Welcome to Event Manager!
      </div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Username: </label>
        <input
          type="text"
          value={username}
          placeholder="enter a username"
          onChange={({ target }) => setUsername(target.value)}
        />
        <div>
          <label htmlFor="password">password: </label>
          <input
            type="password"
            value={password}
            placeholder="enter a password"
            onChange={({ target }) => setPassword(target.value)}
          />
        </div>
        <button className='btn btn--alt' type="submit">Login</button>
        <button className='btn btn--alt' onClick = {goRegister}>Register Now</button>
      </form>
    </div>
  );
};

export default Login;
