import React, { useState, useEffect } from "react";
import axios from "axios";
import {Routes, Route, useNavigate} from 'react-router-dom';
import {getUser, getToken, setUserSession, resetUserSession} from "./Service/AuthService";

const verifyTokenAPIURL = "https://xhetcinp85.execute-api.us-west-1.amazonaws.com/prod/verify";

export const Base = () => {

  const [isAuthenticating, setAuthenticating] = useState(true);
  useEffect(() => {
    const token = getToken();
    const user = getUser();
    if (token === null || user === null) {
      resetUserSession();
      alert("Error: Session expired. Please relogin.");
      return;
    }

    const requestConfig = {
      headers: {
        'x-api-key': 'n4mlBUgJbT3YSFHliX5tTeakXeB760x9GrdrVJIi'
      }
    }
    const requestBody = {
      user: user,
      token: token
    }

    axios.post(verifyTokenAPIURL, requestBody, requestConfig).then(response => {
      setUserSession(response.data.user, response.data.token);
      setAuthenticating(false);
    }).catch(() => {
      resetUserSession();
      setAuthenticating(false);
      alert("Error: Session expired. Please relogin.");
    })
  }, []);

  const token = getToken();
  if (isAuthenticating && token) {
    return <div className="content">Authenticating...</div>
  }

  return (
    <div>
      Hello
    </div>
  );
};

export default Base;
