import React, { useState, useEffect } from "react";
import axios from "axios";
import {Routes, Route, useNavigate} from 'react-router-dom';
import { url } from '../App';

const loginUrl = url + "/login";

export const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState();
  const navigate = useNavigate();

  // check if there is a user already logged in from  previous activity.
  useEffect(() => {
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      setUsername(foundUser);
      navigate("/home/ac");
    }
  }, []);

  // login the user after login-form completion
  const handleSubmit = async e => {
    e.preventDefault();
    if (username.trim() === '' || password.trim() === ''){
      alert('All fields are required');
      return;
    }
    const requestConfig = {
      headers: {
        'x-api-key': 'n4mlBUgJbT3YSFHliX5tTeakXeB760x9GrdrVJIi'
      }
    }
    const requestBody = {
      username: username,
      password: password
    }
    axios.post(loginUrl, requestBody, requestConfig).then(response => {
      localStorage.setItem("user", JSON.stringify(username));
        // go to homepage
        navigate("/home/ac");
    }).catch(error => {
      alert(error);
    });
    /**
    // send the id and password to the server
    axios.post(url+"/login", {
      params: {
        username: username,
        password: password
      }
    }).then((response) => {
      if(response.data.error === "") {
        // set the state of the user
        setUsername(response.data.user_id);
        // store the user in localStorage
        localStorage.setItem("user", JSON.stringify(response.data.user_id));
        // go to homepage
        navigate("/home/ac");
      }
      else alert("Error: " + response.data.error);
    }).catch(error => {
      alert(error);
    });
    **/
  };

  const goRegister = () => {
    navigate("/register");
  };

  // if there's no user, show the login form
  return (
    <div className='card'>
      <div>
        Welcome to Event Manager!
      </div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Username: </label>
        <input
          type="text"
          value={username}
          placeholder="enter a username"
          onChange={({ target }) => setUsername(target.value)}
        />
        <div>
          <label htmlFor="password">password: </label>
          <input
            type="password"
            value={password}
            placeholder="enter a password"
            onChange={({ target }) => setPassword(target.value)}
          />
        </div>
        <button className='btn btn--alt' type="submit">Login</button>
        <button className='btn btn--alt' onClick = {goRegister}>Register Now</button>
      </form>
    </div>
  );
};

export default Login;
