import {useParams} from "react-router-dom";
import React, { useState, useEffect } from "react";
import axios from "axios";
import EventMaker from './Component2/EventMaker';
import {url} from '../App';


export const ViewClubAnnouncements = () => {
  let {clubID} = useParams();
  //club's event parameters
  const [annos, setAnnos] = useState([]);
  const [filter, setFilter] = useState("");
  const [limit, setLimit] = useState("");


  useEffect(() => {
    async function getEvents(){
      const response = await axios.get(url+"/announcement", {
        params:{
          user_id: JSON.parse(localStorage.getItem("user")),
          club_id: clubID,
          filter: filter,
          limit: limit
        }
      }).then(resposne => {
        if (response.error == "") setAnnos(response.announcements_list);
        else alert("Error: " + response.error);
      }).catch (error => {
        alert(error);
        navigate(-1);
      });
    };
    getEvents();
  }, [])

  // used to create an event
  const[annoMakerOpen, setAnnoMakerOpen] = useState(false);
  function openAnnoMaker(){
    setAnnoMakerOpen(true);
  }
  function closeAnnoMaker(){
    setAnnoMakerOpen(false);
  }

  return (
    <div>
        <div>
          <form>
            <label htmlFor="filter">Search by name: </label>
            <input
              type="text"
              value={filter}
              placeholder="search club by name"
              onChange={({ target }) => setFilter(target.value)}
              />
            <button type ="submit">Enter</button>
          </form>
        </div>
        <div>
          <form>
            <label htmlFor="limit">Change viewing limit: </label>
            <input
              type="text"
              value={limit}
              placeholder="change the viewing limit"
              onChange={({ target }) => setLimit(target.value)}
              />
            <button type ="submit">Enter</button>
          </form>
        </div>
        <div>
          <p>{clubID}'s Announcements:</p>
        </div>
        <div className='actions'>
          <button className='btn' onClick={openAnnoMaker}>Create an Announcement</button>
        </div>
        {eventMakerOpen && <EventMaker onCancel={closeEventMaker}/>}
        {eventMakerOpen && <Backdrop onCancel={closeEventMaker}/>}
        <div>
          {events.map((event) => (
          <div className = "card">
            <p>{event.title} (@{event.event_id})</p>
            <p>Organized By {event.club_id}</p>
            <p>Planned Date: {event.planned_date}</p>
            <p>Event Created: {event.date_generated}</p>
            <p>Capacity: {event.num_attending}/{event.event_capacity}</p>
            <div>
              <div>
                Description
              </div>
              <div>
                {event.description}
              </div>
            </div>
            <div>
              <div>
                People Involved
              </div>
              <div>
                {event.people_involved.map((person) => (
                  <p>(person)</p>
                ))}
              </div>
            </div>
            <button className='btn' onClick={() => handleJoinStatus(event)}>Mark Event</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewClubAnnuncements;
