import React, { useState, useEffect } from "react";
import axios from "axios";
import {url, navigate} from '../App';

export const ViewClubs = () => {
  const [filter, setFilter] = useEffect("");
  const [clubArr, setClubArr] = useEffect([]);

  useEffect(() => {
    async function getClubs(){
      const response = await axios.get(url+"/club", {
        params:{
          user_id: JSON.stringify(localStore.getItem("user")),
          filter: filter
        }
      }).then(response => {
        if (response.error == "") setClubArr(response.club_list);
        else {  // display error, then go to previous page
          alert("Error: " + response.error);
          navigate(-1);
        }
      }).catch(error => {
        alert(error);
      })
    }
  })

  function showClub(club) {
    navigate("/vclubs/" + club.id);
  }

  const handleSelect = (e) => {
    setFilter(e.target.value);
  }


  return (
    <div>
      <form>
        <label htmlFor="filter">Search: </label>
        <input
          type="text"
          value={filter}
          placeholder="search club by name"
          onChange={({ target }) => setFilter(target.value)}
        />
        <button type ="submit">Enter</button>
      </form>
      {clubArr.map((club) => (
        <div className = "card">
          <a href="" onClick={() => showClub(club)}>{club.name}</a>
        </div>
      ))}
    </div>
  );
};

export default ViewClubs;
