import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate} from 'react-router-dom';
import axios from 'axios';

import CardAC from './Component/CardAC';
import Delete from './Component/Delete';
import Login from './Component/Login';
import Profile from './Component/Profile';
import Register from './Component/Register';
import Settings from './Component/Settings';
import Signout from './Component/Signout';
import Sidebar from './Component/Sidebar';
import ViewClubs from './Component/ViewClubs';
import ViewClubEvents from './Component/ViewClubEvents';
import ViewClubAnnouncements from './Component/ViewClubAnnouncements';

import { getUser, getToken, setUserSession, resetUserSession } from './Component/Service/AuthService';

import PublicRoute from './Component/Route/PublicRoute';
import PrivateRoute from './Component/Route/PrivateRoute';

const verifyTokenAPIURL = "https://xhetcinp85.execute-api.us-west-1.amazonaws.com/prod/verify";

function App() {
  const [isAuthenticating, setAuthenticating] = useState(true);

  useEffect(() => {
    const token = getToken();
    if (!token) {
      setAuthenticating(false);
      return;
    }

    const requestConfig = {
      headers: {
        'x-api-key': 'n4mlBUgJbT3YSFHliX5tTeakXeB760x9GrdrVJIi',
      },
    };
    const requestBody = {
      user: getUser(),
      token: token,
    };

    axios
      .post(verifyTokenAPIURL, requestBody, requestConfig)
      .then((response) => {
        setUserSession(response.data.user, response.data.token);
      })
      .catch(() => {
        resetUserSession();
      })
      .finally(() => {
        setAuthenticating(false);
      });
  }, []);

  if (isAuthenticating) {
    return <div className="content">Authenticating...</div>;
  }

  const token = getToken();

  const renderPublic = (publicComponent) => {
    return token ? <Navigate to="/home" /> : publicComponent;
  };

  const renderPrivate = (privateComponent) => {
    return !token ? privateComponent : <Navigate to="/login" />;
  };


  return (
    <BrowserRouter>
      <Routes>
        <Route path="/login" element={renderPublic(<Login />)} />
        <Route path="/register" element={renderPublic(<Register />)} />

        <Route path="/home" element={renderPrivate(<Sidebar />)} />
          <Route path="/ac" element={renderPrivate(<CardAC />)} />
          <Route path="/clubs" element={renderPrivate(<ViewClubs />)} />
            <Route path="/:clubID/events" element={renderPrivate(<ViewClubEvents />)} />
            <Route path="/:clubID/announcements" element={renderPrivate(<ViewClubAnnouncements />)} />
          <Route path="/profile" element={renderPrivate(<Profile />)} />
          <Route path="/settings" element={renderPrivate(<Settings />)} />
            <Route path="/delete" element={renderPrivate(<Delete />)} />
            <Route path="/signout" element={renderPrivate(<Signout />)} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
