import {useParams} from "react-router-dom";
import React, { useState, useEffect } from "react";
import axios from "axios";
import EventMaker from './EventMaker';
import Backdrop from './Component2/Backdrop';
import {url} from '../App';
import PromoteGenMember from './Component2/PromoteGenMember';

export const ViewClubSingle = () => {
  let {clubID} = useParams();
  // club parameters
  const [clubName, setClubName] = useState("");
  const [clubDesc, setClubDesc] = useState("");
  const [boardMembers, setBoardMembers] = useState([]);
  const [genMembers, setGenMembers] = useState([]);
  //club's event parameters
  const [events, setEvents] = useState([]);
  const [filter, setFilter] = useState("");
  const [limit, setLimit] = useState("");

  // used for promoting a general member -> board member
  const [promoteMemOpen, setPromoteMemOpen] = useState(false);
  function openPromoteMem(){
    setPromoteMemOpen(true);
  }
  function closePromoteMem(){
    setPromoteMemOpen(false);
  }

  useEffect(() => {
    async function getClub(){
      const response = await axios.get(url+"/clubs", {
        params:{
          id: clubID
        }
      });
      setClubName(response.name);
      setClubDesc(response.description);
      setBoardMembers(response.board_members);
      setGenMembers(response.general_members);
    };
    async function getEvents(){
      const response = await axios.get(url+"/event", {
        params:{
          user_id: localStorage.getItem("user"),
          club_id: clubID,
          filter: filter,
          limit: limit
        }
      }).then(resposne => {
        if (response.error == "") setEvents(response.event_list);
        else alert("Error: " + response.error);
      }).catch (error => {
        alert(error);
        navigate(-1);
      });
    };
    getClub();
    getEvents();
  }, [])

  return (
    <div>
      <div className='card'>
        <div>
          {clubName} (@{clubID})
        </div>
        <div>
          Description
        </div>
        <div>
          {clubDesc}
        </div>
        <div>
        </div>
      </div>
      <div className = 'card'>
        <div>
        Board Members
        </div>
        <div>
        {boardMembers.map(member => <p>{member}</p>)}
        </div>
      </div>
      <div className = 'card'>
        <div>
        General Members
        </div>
        <div>
          {genMembers.map((member) => <p>member</p> )}
        </div>
        <div>
        <button className='btn btn--alt'onClick={openPromoteMem}>Promote Member</button>
        </div>
        <div>
          <form>
            <label htmlFor="filter">Search by name: </label>
            <input
              type="text"
              value={filter}
              placeholder="search club by name"
              onChange={({ target }) => setFilter(target.value)}
              />
            <button type ="submit">Enter</button>
          </form>
        </div>
        <form>
          <label htmlFor="limit">Change viewing limit: </label>
          <input
            type="text"
            value={limit}
            placeholder="search club by name"
            onChange={({ target }) => setLimit(target.value)}
          />
          <button type ="submit">Enter</button>
        </form>
      </div>
    </div>
  );
};

export default ViewClubSingle;
