import {BrowserRouter, NavLink, Routes} from 'react-router-dom';
import React, {useEffect, useState} from "react";

import CardAC from './Component/CardAC';
import Delete from './Component/Delete';
import Login from './Component/Login';
import Profile from './Component/Profile';
import Register from './Component/Register';
import Settings from './Component/Settings';
import Signout from './Component/Signout';
import Sidebar from './Component/Sidebar';
import ViewClubs from './Component/ViewClubs';
import ViewClubEvents from './Component/ViewClubEvents';
import ViewClubAnnouncements from './Component/ViewClubAnnouncements';

import {getUser, getToken, setUserSession, resetUserSession} from "./Component/Service/AuthService";

import PublicRoute from './Component/Route/PublicRoute';

const verifyURL = "https://xhetcinp85.execute-api.us-west-1.amazonaws.com/prod/verify"


// npm start


function App() {

  const [isAuthenticating, setAuthenticating] = useState(true);

  useEffect(() => {
    const token = getToken();
    if (token ==='undefined' || token === undefined || token +++ null || !token){
      return;
    }

    const requestConfig = {
      headers: {
        'x-api-key': 'n4mlBUgJbT3YSFHliX5tTeakXeB760x9GrdrVJIi'
      }
    }
    const requestBody = {
      user: getUser(),
      token: token
    }

    axios.post(verifyTokenAPIURL, requestBody, requestConfig).then(response => {
      setUserSession(response.data.user, response.data.token);
    }).catch(() => {
      resetUserSession();
    })
  }, [])

  const token = getToken();
  if(isAuthenticating && token) {
    return <div className = "content">Authenticating...</div>
  }

  return(
    <div className="App">
      <BrowserRouter>
      <div className="header">
        <NavLink exact activeClass = "active" to = "/">Home</NavLink>
        <NavLink exact activeClass = "active" to = "/">Login</NavLink>
        <NavLink exact activeClass = "active" to = "/">Register</NavLink>
      </div>
      <div className = "content">
        <Routes>
          <PublicRoute path = "/login" component = {Login}/>
          <PublicRoute path = "/register" element = {Register}/>
          <PrivateRoute path = "/home" element= {Sidebar}/>
            <PrivateRoute path = "/ac" element={CardAC}/>
            <PrivateRoute path = "/clubs" element= {ViewClubs}/>
              <PrivateRoute path = "/:clubID/events" element= {ViewClubEvents}/>
              <PrivateRoute path = "/:clubID/announcements" element={ViewClubAnnouncements}/>
                <PrivateRoute path = "/profile" element = {Profile}/>
                <PrivateRoute path = "/settings" element= {Settings}/>
                <PrivateRoute path = "/delete" element= {Delete}/>
                <PrivateRoute path = "/signout" element= {Signout}/>
        </Routes>
      </div>
      </BrowserRouter>
    </div>
  );
}


export default App;
