import React, {useState, useEffect} from 'react';
import {BrowserRouter, Routes, Route, useNavigate} from 'react-router-dom';
import axios from "axios";
import Base from './Component/Base';
import Login from './Component/Login';
import Register from './Component/Register';
import Sidebar from './Component/Sidebar';
import CardAC from './Component/CardAC';
import ViewClubs from './Component/ViewClubs';
import ViewClubEvents from './Component/ViewClubEvents';
import ViewClubAnnouncements from './Component/ViewClubAnnouncements';
import Settings from './Component/Settings';
import Signout from './Component/Signout';
import {getUser, getToken, setUserSession, resetUserSession} from "./Component/Service/AuthService";


const verifyTokenAPIURL = "https://xhetcinp85.execute-api.us-west-1.amazonaws.com/prod/verify";

function App() {
  const [isAuthenticating, setAuthenticating] = useState(true);
  useEffect(() => {
    const token = getToken();
    const user = getUser();
    if (token === null || user === null) {
      resetUserSession();
      alert("Error: Session expired. Please relogin.");
      return;
    }

    const requestConfig = {
      headers: {
        'x-api-key': 'n4mlBUgJbT3YSFHliX5tTeakXeB760x9GrdrVJIi'
      }
    }
    const requestBody = {
      user: user,
      token: token
    }

    axios.post(verifyTokenAPIURL, requestBody, requestConfig).then(response => {
      setUserSession(response.data.user, response.data.token);
      setAuthenticating(false);
    }).catch(() => {
      resetUserSession();
      setAuthenticating(false);
      alert("Error: Session expired. Please relogin.");
    })
  }, []);

  const token = getToken();
  if (isAuthenticating && token) {
    return <div className="content">Authenticating...</div>
  }

  return(
    <BrowserRouter>
        <Routes>
          <Route path = "" element = {<Base />} />
            <Route path = "login" element = {<Login />} />
            <Route path = "register" element = {<Register />} />
            <Route path = "home/" element= {<Sidebar />} >
              <Route path = "ac" element={<CardAC/>} />
              <Route path = "clubs" element= {<ViewClubs />} />
              <Route path = "clubs/:clubID/events" element= {<ViewClubEvents />} />
              <Route path = "clubs/:clubID/announcements" element={<ViewClubAnnouncements/>} />
              <Route path = "settings" element= {<Settings />} />
              <Route path = "signout" element= {<Signout />} />
          </Route>
        </Routes>
    </BrowserRouter>
  );
}


export default App;
